void main() {
  print('Hello, World!');
  final input =
      '';
  var currentIndex = 3;
  int? matchIndex;
  while (matchIndex == null && currentIndex < input.length) {
    final lastFour = input.substring(currentIndex - 3, currentIndex + 1);
    if (lastFour.split('').toSet().length == 4) {
      matchIndex = currentIndex;
    }
    currentIndex++;
  }

  if (matchIndex != null) {
    print('Found match at index ${matchIndex + 1}');
  } else {
    print('No match found');
  }
}
